// Welcome.js

import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

function Welcome() {
  const [message, setMessage] = useState("");

  useEffect(() => {
    // Make sure to add withCredentials: true to send cookies or session data with request
    axios.get("http://127.0.0.1:8000/welcome/", { withCredentials: true })  
      .then(response => setMessage(response.data.message))
      .catch(() => setMessage("Welcome, Guest!"));
  }, []);

  return (
    <div>
      <h1>{message}</h1>
      <Link to="/old-items"><button>Old Items</button></Link>
      <Link to="/new-items"><button>New Items</button></Link>
    </div>
  );
}

export default Welcome;
