import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';  // React Router v6 এ useNavigate ব্যবহার করুন

const Login = () => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();  // useNavigate দিয়ে রিডাইরেক্ট করা হবে

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://127.0.0.1:8000/auth/login/', {
        phone_number: phoneNumber,
        password: password
      });

      // যদি লগিন সফল হয়, তখন টোকেন বা সাফল্য মেসেজ পাওয়া যাবে
      console.log(response.data);  // এটি হতে পারে "Login successful" মেসেজ
     
      // টোকেন বা প্রয়োজনীয় তথ্য localStorage এ সংরক্ষণ করা যেতে পারে (যদি প্রয়োজন হয়)
      localStorage.setItem('token', response.data.token);

      // লগিন সফল হলে ওয়েলকাম পেজে রিডাইরেক্ট করা হবে
      navigate('/welcome');  // এখানে useNavigate দিয়ে রিডাইরেক্ট করা হবে
    } catch (err) {
      setError(err.response.data.error);
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Phone Number"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Login</button>
      </form>
      {error && <p>{error}</p>}
    </div>
  );
};

export default Login;
