import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';  // Import useHistory hook from react-router-dom

const Login = () => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const history = useHistory();  // Initialize history for redirection

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://127.0.0.1:8000/auth/login/', {
        phone_number: phoneNumber,
        password: password
      });

      // Assuming the backend sends a success message and token
      console.log(response.data);  // This can be the message like "Login successful"
      
      // Save token or any relevant info into localStorage (optional)
      localStorage.setItem('token', response.data.token);

      // Redirect to the welcome page after successful login
      history.push('/welcome');
    } catch (err) {
      setError(err.response.data.error);
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Phone Number"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Login</button>
      </form>
      {error && <p>{error}</p>}
    </div>
  );
};

export default Login;
